{"settings":"{
    "editor.guides.bracketPairs": "active",
    "java.debug.settings.hotCodeReplace": "auto",
    "java.sources.organizeImports.staticStarThreshold": 1,
    "editor.codeActionsOnSave": {
        "source.organizeImports": "explicit"
    },
    "editor.rulers": [
        {
            "column": 80,
            "color": "#00FF0010"
        },
        {
            "column": 100,
            "color": "#BDB76B15"
        },
        {
            "column": 120,
            "color": "#FA807219"
        }
    ],
    "editor.unicodeHighlight.includeComments": true,
    "workbench.colorCustomizations": {
        "[Default Dark+][Visual Studio Dark]": {
            "tab.activeBorder": "#0F0"
        },
        "[Default Dark Modern]": {
            "tab.activeBorderTop": "#00FF00",
            "tab.unfocusedActiveBorderTop": "#00FF0088",
            "textCodeBlock.background": "#00000055"
        },
        "editor.wordHighlightStrongBorder": "#FF6347",
        "editor.wordHighlightBorder": "#FFD700",
        "editor.selectionHighlightBorder": "#A9A9A9"
    },
    "workbench.tree.indent": 20,
    "cSpell.diagnosticLevel": "Hint",
    "trailing-spaces.includeEmptyLines": false,
    "terminal.integrated.profiles.linux": {
        "bash": {
            "path": "bash",
            "icon": "terminal-bash"
        },
        "zsh": {
            "path": "zsh"
        },
        "fish": {
            "path": "fish"
        },
        "tmux": {
            "path": "tmux",
            "icon": "terminal-tmux"
        },
        "pwsh": {
            "path": "pwsh",
            "icon": "terminal-powershell"
        },
        "JavaSE-1.8": {
            "overrideName": true,
            "env": {
                "JAVA_HOME": "/home/cj/.config/Code/User/globalStorage/pleiades.java-extension-pack-jdk/java/8",
                "PATH": "/home/cj/.config/Code/User/globalStorage/pleiades.java-extension-pack-jdk/java/8/bin:${env:PATH}"
            },
            "path": "bash",
            "args": [
                "--rcfile",
                "~/.bashrc_jdkauto"
            ]
        },
        "JavaSE-11": {
            "overrideName": true,
            "env": {
                "JAVA_HOME": "/usr/lib/jvm/java-11-openjdk-amd64",
                "PATH": "/usr/lib/jvm/java-11-openjdk-amd64/bin:${env:PATH}"
            },
            "path": "bash",
            "args": [
                "--rcfile",
                "~/.bashrc_jdkauto"
            ]
        }
    },
    "redhat.telemetry.enabled": true,
    "glassit.alpha": 220,
    "[python]": {
        "diffEditor.ignoreTrimWhitespace": false,
        "gitlens.codeLens.symbolScopes": [
            "!Module"
        ]
    },
    "files.associations": {
        "*.py": "python"
    },
    "files.autoSave": "afterDelay",
    "files.autoSaveDelay": 600,
    "symbols.hidesExplorerArrows": false,
    "explorer.confirmDragAndDrop": false,
    "sqltools.connections": [],
    "hediet.vscode-drawio.resizeImages": null,
    "workbench.colorTheme": "Ayu One Dark Pro | Dark",
    "diffEditor.ignoreTrimWhitespace": false,
    "workbench.iconTheme": "vscode-icons",
    "chatgpt.lang": "en",
    "liveSassCompile.settings.excludeList": [


        "**/node_modules/**",
        ".vscode/**"
    ],
    "liveSassCompile.settings.formats": [

        {
            "format": "expanded",
            "extensionName": ".css",
            "savePath": null
        }
    ],
    "git.autofetch": true,
    "extensions.experimental.affinity": {
        "asvetliakov.vscode-neovim": 1
    },
    "editor.stickyScroll.enabled": true,
    "debug.onTaskErrors": "debugAnyway"
}"}
